import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject } from 'rxjs';
import { tap, map } from 'rxjs/operators';
import { Personne } from '../models/personne.model';

@Injectable({
  providedIn: 'root'
})
export class PersonneService {
  private apiUrl = 'http://localhost:8080/api/personnes';
  private personnesSubject = new BehaviorSubject<Personne[]>([]);
  public personnes$ = this.personnesSubject.asObservable();

  constructor(private http: HttpClient) {
    this.rafraichirPersonnes();
  }

  // Fonction pour calculer l'âge à partir de la date de naissance
  private calculerAge(dateNaissance: string): number {
    if (!dateNaissance) return 0;
    
    const dateNaissanceObj = new Date(dateNaissance);
    const aujourdhui = new Date();
    
    let age = aujourdhui.getFullYear() - dateNaissanceObj.getFullYear();
    const mois = aujourdhui.getMonth() - dateNaissanceObj.getMonth();
    
    if (mois < 0 || (mois === 0 && aujourdhui.getDate() < dateNaissanceObj.getDate())) {
      age--;
    }
    
    return age;
  }

  obtenirToutesPersonnes(): Observable<Personne[]> {
    return this.http.get<Personne[]>(this.apiUrl)
      .pipe(
        map(personnes => {
          // Vérifier et corriger les problèmes potentiels avec l'âge et emploisActuels
          return personnes.map(p => {
            console.log(`Personne ${p.nom} ${p.prenom} - Age type: ${typeof p.age}, value: ${p.age}`);
            console.log(`Emplois actuels reçus: ${JSON.stringify(p.emploisActuels)}`);
            
            // Créer une copie de la personne pour les modifications
            const personneCorrigee: Personne = { ...p };
            
            // S'assurer que l'âge est un nombre
            if (p.age === undefined || p.age === null || isNaN(Number(p.age)) || Number(p.age) === 0) {
              console.log(` - Problème avec l'âge, valeur reçue: ${p.age}, calcul à partir de dateNaissance: ${p.dateNaissance}`);
              personneCorrigee.age = this.calculerAge(p.dateNaissance);
            } else {
              personneCorrigee.age = Number(p.age);
            }
            
            // S'assurer que emploisActuels est un tableau non-null
            if (!Array.isArray(p.emploisActuels)) {
              console.log(` - Problème avec emploisActuels, valeur reçue:`, p.emploisActuels);
              personneCorrigee.emploisActuels = [];
            }
            
            return personneCorrigee;
          });
        }),
        tap(personnes => {
          console.log('Personnes traitées (après corrections):', personnes);
          this.personnesSubject.next(personnes);
        })
      );
  }

  obtenirPersonnesParEntreprise(nomEntreprise: string): Observable<Personne[]> {
    return this.http.get<Personne[]>(`${this.apiUrl}/entreprise/${encodeURIComponent(nomEntreprise)}`)
      .pipe(
        map(personnes => {
          return personnes.map(p => {
            console.log(`Personne ${p.nom} ${p.prenom} (entreprise: ${nomEntreprise}) - Age: ${p.age}`);
            
            const personneCorrigee: Personne = { ...p };
            
            if (p.age === undefined || p.age === null || isNaN(Number(p.age)) || Number(p.age) === 0) {
              personneCorrigee.age = this.calculerAge(p.dateNaissance);
            } else {
              personneCorrigee.age = Number(p.age);
            }
            
            if (!Array.isArray(p.emploisActuels)) {
              personneCorrigee.emploisActuels = [];
            }
            
            return personneCorrigee;
          });
        })
      );
  }

  obtenirPersonneParId(id: number): Observable<Personne> {
    return this.http.get<Personne>(`${this.apiUrl}/${id}`)
      .pipe(
        map(personne => {
          console.log(`Personne par ID ${id} - Age: ${personne.age}, Type: ${typeof personne.age}`);
          console.log(`Emplois actuels reçus: ${JSON.stringify(personne.emploisActuels)}`);
          
          // Créer une copie pour les modifications
          const personneCorrigee: Personne = { ...personne };
          
          // S'assurer que l'âge est un nombre
          if (personne.age === undefined || personne.age === null || isNaN(Number(personne.age)) || Number(personne.age) === 0) {
            console.log(` - Problème avec l'âge, valeur reçue: ${personne.age}, calcul à partir de dateNaissance: ${personne.dateNaissance}`);
            personneCorrigee.age = this.calculerAge(personne.dateNaissance);
          } else {
            personneCorrigee.age = Number(personne.age);
          }
          
          // S'assurer que emploisActuels est un tableau non-null
          if (!Array.isArray(personne.emploisActuels)) {
            console.log(` - Problème avec emploisActuels, valeur reçue:`, personne.emploisActuels);
            personneCorrigee.emploisActuels = [];
          } else if (personne.emploisActuels && personne.emploisActuels.length > 0) {
            console.log(` - Nombre d'emplois actuels: ${personne.emploisActuels.length}`);
            personne.emploisActuels.forEach((emploi, index) => {
              console.log(`   Emploi ${index + 1}: ${emploi.titrePoste} chez ${emploi.nomEntreprise}`);
            });
          }
          
          return personneCorrigee;
        }),
        tap(personne => {
          console.log(`Personne récupérée par ID ${id} (après traitement):`, personne);
          
          // Mettre à jour la personne dans la liste locale si elle existe
          const personnes = this.personnesSubject.value;
          const index = personnes.findIndex(p => p.id === id);
          if (index !== -1) {
            personnes[index] = personne;
            this.personnesSubject.next([...personnes]);
          }
        })
      );
  }

  creerPersonne(personne: Personne): Observable<any> {
    return this.http.post(this.apiUrl, personne)
      .pipe(tap(() => this.rafraichirPersonnes()));
  }

  supprimerPersonne(id: number): Observable<any> {
    return this.http.delete(`${this.apiUrl}/${id}`)
      .pipe(tap(() => this.rafraichirPersonnes()));
  }
  
  rafraichirPersonnes(): void {
    this.http.get<Personne[]>(this.apiUrl).subscribe({
      next: (personnes) => {
        // Traiter les personnes pour s'assurer que l'âge et emploisActuels sont corrects
        const personnesTraitees = personnes.map(p => {
          console.log(`Rafraîchissement - Personne ${p.nom} ${p.prenom} - Age: ${p.age}`);
          console.log(`Emplois actuels reçus: ${JSON.stringify(p.emploisActuels)}`);
          
          // Créer une copie pour les modifications
          const personneCorrigee: Personne = { ...p };
          
          // S'assurer que l'âge est un nombre
          if (p.age === undefined || p.age === null || isNaN(Number(p.age)) || Number(p.age) === 0) {
            console.log(` - Problème avec l'âge, valeur reçue: ${p.age}, calcul à partir de dateNaissance: ${p.dateNaissance}`);
            personneCorrigee.age = this.calculerAge(p.dateNaissance);
          } else {
            personneCorrigee.age = Number(p.age);
          }
          
          // S'assurer que emploisActuels est un tableau non-null
          if (!Array.isArray(p.emploisActuels)) {
            console.log(` - Problème avec emploisActuels, valeur reçue:`, p.emploisActuels);
            personneCorrigee.emploisActuels = [];
          } else if (p.emploisActuels && p.emploisActuels.length > 0) {
            console.log(` - Nombre d'emplois actuels: ${p.emploisActuels.length}`);
          }
          
          return personneCorrigee;
        });
        
        console.log('Rafraîchissement des personnes (après traitement):', personnesTraitees);
        this.personnesSubject.next(personnesTraitees);
      },
      error: (err) => {
        console.error('Erreur lors du rafraîchissement des personnes:', err);
      }
    });
  }
}
